mapper.select:
select item_seq, brand_seq, category_id, item_code, item_name, item_price, item_color, item_size, item_create_date
from item
where item_seq = #{itemSeq}

mapper.update:
update item
category_id = #{categoryId}, item_code = #{itemCode}, item_name = #{itemName}, item_price = #{itemPrice}, item_color = #{itemColor}, item_size = #{itemSize},
where item_seq = #{itemSeq}
