mapper.select:
select seller_seq, brand_seq, seller_id, seller_name, seller_phone, seller_address, seller_create_date
from seller
where seller_seq = #{sellerSeq}

mapper.update:
update seller
seller_pwd = #{sellerPwd}, seller_name = #{sellerName}, seller_phone = #{sellerPhone}, seller_address = #{sellerAddress},
where seller_id = #{sellerId}
