mapper.select:
select stock_history_seq, item_seq, stock_history_change_qty, stock_history_create_date
from stock_history
where stock_history_seq = #{stockHistorySeq}

select stock_history_seq, item_seq, stock_history_change_qty, stock_history_create_date
from stock_history
where item_seq = #{itemSeq}

mapper.update:
update stock_history
item_seq = #{itemSeq}, stock_history_change_qty = #{stockHistoryChangeQty}, stock_history_create_date = #{stockHistoryCreateDate},
where stock_history_seq = #{stockHistorySeq}
